void PioneerAmplifier::sendRaw(const microseconds_t intro_P[], size_t lengthIntro, const microseconds_t repeat_P[],
    size_t lengthRepeat, frequency_t frequency, unsigned times) {
    microseconds_t intro[lengthIntro];
    microseconds_t repeat[lengthRepeat];
    memcpy_PF(intro, (uint_farptr_t)intro_P, sizeof(microseconds_t) * lengthIntro);
    memcpy_PF(repeat, (uint_farptr_t)repeat_P, sizeof(microseconds_t) * lengthRepeat);

    IrSignal irSignal(intro, lengthIntro, repeat, lengthRepeat, NULL, 0U, frequency);
    _irsend->sendIrSignal(irSignal, times);
}

void PioneerAmplifier::power(int times) {
    sendRaw(NULL, 0U, repeat_POWER, 68U, 40000U, times);
}

void PioneerAmplifier::volumeUp(int times) {
    sendRaw(NULL, 0U, repeat_VOLUME__, 68U, 40000U, times);
}

void PioneerAmplifier::volumeDown(int times) {
    sendRaw(NULL, 0U, repeat_VOLUME___1, 68U, 40000U, times);
}

